import React, {Component} from 'react';

class Contact extends Component{

  state(avatar, name, status, msg ){
   this.avatar = avatar;
   this.name = name;
   this.status = status;
   this.msg = msg;

  }


  render(){
    return(
  <div className="contact">
    <img className = 'avatar' src = {this.avatar = 'https://cdn.icon-icons.com/icons2/884/PNG/128/person_4_icon-icons.com_68900.png'}></img>;
    <div className= 'container'>
      <h4 className= 'name' >{this.name = 'Abdel'}</h4>
      <div className = 'status'>
        {this.status}
        <p className = 'status-text'>{this.msg = 'coucou'}</p> 
      </div>
    </div>
  </div> 
    )

  }
}



